package tv.superawesome.sdk.publisher.common.components

import android.graphics.Bitmap
import android.graphics.BitmapFactory
import android.graphics.Canvas
import android.graphics.Paint
import android.graphics.PorterDuff
import android.graphics.PorterDuffXfermode
import android.graphics.Rect
import android.graphics.RectF
import android.util.Base64

interface ImageProviderType {
    fun bumperPoweredByImage(): Bitmap
    fun bumperBackgroundImage(): Bitmap

    fun padlockImage(): Bitmap
    fun closeImage(): Bitmap
    fun videoGradientBitmap(): Bitmap
    fun createBitmap(width: Int, height: Int, color: Int, radius: Float): Bitmap
}

class ImageProvider : ImageProviderType {
    override fun bumperPoweredByImage(): Bitmap {
        val imageString =
            ""
        return createImageBitmap(imageString)
    }

    override fun bumperBackgroundImage(): Bitmap {
        val imageString =
            ""
        return createImageBitmap(imageString)
    }

    override fun padlockImage(): Bitmap {
        val imageString =
            ""
        return createImageBitmap(imageString)
    }

    override fun closeImage(): Bitmap {
        val imageString =
            ""
        return createImageBitmap(imageString)
    }

    override fun videoGradientBitmap(): Bitmap {
        val imageString =
            "iVBORw0KGgoAAAANSUhEUgAAAAoAAAAICAYAAADA+m62AAAAAXNSR0IArs4c6QAAABxpRE9UAAAAAgAAAAAAAAAEAAAAKAAAAAQAAAAEAAAAYzTSV/QAAAAvSURBVCgVYmBgYBAhEjOYAhXiwmZIcgyhQE4YEINodIwszlAGVADDpUhsmBiYBgAAAP//1nMT5wAAAChJREFUY2BgYJiKhKcB2SCMLgbiM2wF4m1EYIarQEXXiMAM34CKCGIAAN0pshJZ248AAAAASUVORK5CYII="
        return createImageBitmap(imageString)
    }

    override fun createBitmap(width: Int, height: Int, color: Int, radius: Float): Bitmap {
        val output = Bitmap.createBitmap(width, height, Bitmap.Config.ARGB_8888)
        val canvas = Canvas(output)
        val paint = Paint()
        val rect = Rect(0, 0, output.width, output.height)
        val rectF = RectF(rect)
        paint.isAntiAlias = true
        canvas.drawARGB(0, 0, 0, 0)
        paint.color = color
        canvas.drawRoundRect(rectF, radius, radius, paint)
        paint.xfermode = PorterDuffXfermode(PorterDuff.Mode.SRC_IN)
        canvas.drawBitmap(output, rect, rect, paint)
        return output
    }

    private fun createImageBitmap(imageString: String): Bitmap {
        val encodeByte = Base64.decode(imageString, Base64.DEFAULT)
        return BitmapFactory.decodeByteArray(encodeByte, 0, encodeByte.size)
    }
}
